import React from 'react';

const ERR_SELECT = 'You must choose one option';

class Select extends React.Component {
  handleError = value => {};

  render() {
    const {
      hasError,
      label,
      name,
      onChange,
      options,
      showError,
      type,
      value
    } = this.props;

    const error = this.handleError(value, type);

    const optionsList = options.map(el => <option value={[el]}>el</option>);

    return (
      <div>
        <label htmlFor={name}>{label}</label>
        {showError ? <span>{error}</span> : null}

        <select
          id={name}
          name={name}
          value={value}
          onChange={onChange}
          haserror={hasError(error)}>
          {optionsList}
          {/* <option value='updates'>Updates Only</option>
          <option value='premium'>Premiun Access</option> */}
        </select>
      </div>
    );
  }
}

export default Select;
