import React from "react";

class InputText extends React.Component {
  state = {
    value: ""
  };

  handleChange = event => {
    const { name, value } = event.target;
    const { formsError } = this.state;

    /**
     * Handle Input rules
     * Return String or false
     */
    switch (name) {
      case "fullName":
        formsError[name] = value.length < 3 ? ERR_TEXT : false;
        break;
      case "userId":
        formsError[name] = value.length < 3 ? ERR_TEXT : false;
        break;
      case "email":
        formsError[name] = REGEX_MAIL.test(value) ? false : ERR_MAIL;
        break;
      case "password":
        formsError[name] = value.length < 6 ? ERR_PASS : false;
        break;
      case "passwordCheck":
        formsError[name] = value.length < 6 ? ERR_PASS : false;
        break;
      case "accessType":
        formsError[name] =
          value === "updates"
            ? false
            : value === "premium"
            ? false
            : ERR_SELECT;
        break;
    }

    this.setState({ [name]: value, formsError });
  };

  render() {
    return (
      <div>
        <label htmlFor={this.props.name}>Full Name:</label>
        {/* { this.state.showError && this.state.formsError.fullName } */}
        <input
          id={this.props.name}
          placeholder={this.props.placeholder}
          name={this.props.name}
          type='text'
          value={this.state.value}
          onChange={this.handleChange}
        />
      </div>
    );
  }
}

export default InputText;
