import React from 'react';
import './users.css';

/**
 * DUMMY DATA
 */
const DATA = [
  {
    id: 'john.doe100',
    fullName: 'John Doe',
    email: 'john@mail.com',
    accessType: 'updates'
  },
  {
    id: 'jeanne.doe200',
    fullName: 'Jeanne Doe',
    email: 'jeanne@mail.com',
    accessType: 'updates'
  },
  {
    id: 'bobby.doe300',
    fullName: 'Bobby Doe',
    email: 'bobby@mail.com',
    accessType: 'updates'
  }
];

class UserList extends React.Component {
  state = {
    users: [],
    showUser: false
  };

  componentDidMount() {
    // Get data from the db
    this.setState({ users: DATA });
  }

  // handleShowUser = () => {
  //   console.log('handleShowUser');

  //   // return this.setState({ showUser: !this.state.showUser });
  //   return this.setState({ showUser: !this.state.showUser });
  // };

  handleDelete(id) {
    this.setState({ users: this.state.users.filter(user => user.id !== id) });
  }

  render() {
    return (
      <div>
        <h2>Show a list of users</h2>
        {this.state.users.map(user => (
          <User
            {...user}
            key={user.id}
            delete={() => this.handleDelete(user.id)}
            showUser={this.state.showUser}
            handleShowUser={this.handleShowUser}
          />
        ))}
      </div>
    );
  }
}

export default UserList;

class User extends React.Component {
  state = { showUser: false };

  handleShowUser = () => {
    console.log('handleShowUser');
    // return this.setState({ showUser: !this.state.showUser });
    return this.setState({ showUser: !this.state.showUser });
  };

  render() {
    return (
      <div>
        <p onClick={() => this.handleShowUser}>
          {props.fullName} {props.UserList}
        </p>
        {this.state.showUser && (
          <div>
            <p>
              {props.email} {props.accessType}
            </p>
            <button onClick={props.delete}>Delete User</button>
          </div>
        )}
      </div>
    );
  }
}
