import React from 'react';
import './users.css';
import Input from '../UI/Input';
import UserList from './UserList';

/**
 * DUMMY DATA
 */
const DATA = [
  {
    fullName: 'John Doe',
    userId: 'john.doe100',
    email: 'john@mail.com',
    accessType: 'updates'
  },
  {
    fullName: 'Jeanne Doe',
    userId: 'jeanne.doe200',
    email: 'jeanne@mail.com',
    accessType: 'updates'
  },
  {
    fullName: 'Bobby Doe',
    userId: 'bobby.doe300',
    email: 'bobby@mail.com',
    accessType: 'updates'
  }
];

const Users = class userInput extends React.Component {
  state = {
    fullName: '',
    userId: '',
    email: '',
    password: '',
    passwordCheck: '',
    accessType: 'updates',
    formsError: {
      fullName: false,
      userId: false,
      email: false,
      password: false,
      passwordCheck: false,
      accessType: false
    },
    showError: false
  };

  componentDidMount() {
    // Get data from the db
    this.setState({ users: DATA });
  }

  // case 'accessType':
  //   formsError[name] =
  //     value === 'updates'
  //       ? false
  //       : value === 'premium'
  //       ? false
  //       : ERR_SELECT;

  handleChange = e => this.setState({ [e.target.name]: e.target.value });

  handleSubmit = event => {
    event.preventDefault();
    const { formsError, password, passwordCheck } = this.state;

    // Forms Data to send
    const data = { ...this.state };
    delete data.formsError;
    delete data.passwordCheck;
    delete data.showError;

    // Check if the form has error
    const hasError = Object.values(formsError).find(error => error !== false);
    const dataIsEmpty = Object.values(data).includes('');

    const passIsEqual = password === passwordCheck;

    // If Forms have error show them
    if (hasError || !passIsEqual || dataIsEmpty)
      this.setState({ showError: true });

    // If Forms is valid send data Forms to the server
    if (!hasError && passIsEqual && !dataIsEmpty) {
      const payload = {
        method: 'POST',
        body: data
      };

      fetch('/admin/newuser', payload)
        .then(res => console.log(res))
        .catch(err => console.log(err));

      // FOR TESTING ONLY with dummy data
      this.setState({ users: [...this.state.users, data] });
    }
  };

  handleShowUser() {
    this.setState({ showUser: !this.state.showUser });
  }

  handleDelete(userId) {
    const { users } = this.state;
    this.setState({ users: users.filter(user => user.userId != userId) });
  }

  render() {
    return <div>
        <h1>USERS</h1>
        {this.state.showError && ERR_FORMS}
        <form onSubmit={this.handleSubmit} style={{ display: 'grid' }}>
          <label htmlFor='fullName'>Full Name:</label>
          <Input label='Full Name' placeholder='John Doe' name='fullName' type='text' showError={this.state.showError} hasError={this.hasError} value={this.state.fullName} onChange={this.handleChange} />

          <Input label='User Id' placeholder='john42' name='userId' type='text' showError={this.state.showError} hasError={this.hasError} onChange={this.handleChange} value={this.state.userId} />

          <Input label='Email' placeholder='john.doe@mail.com' name='email' type='email' showError={this.state.showError} hasError={this.hasError} onChange={this.handleChange} value={this.state.email} />

          <Input label='Password' placeholder='Password' name='password' type='password' showError={this.state.showError} hasError={this.hasError} onChange={this.handleChange} value={this.state.currentPassword} />

          <Input label='Comfirm Password' placeholder='Comfirm Password' name='password' type='password' showError={this.state.showError} hasError={this.hasError} onChange={this.handleChange} value={this.state.currentPassword} />

          <label htmlFor='passwordCheck'>Confirm Password:</label>
          {this.state.showError && this.state.formsError.passwordCheck}
          <input id='passwordCheck' placeholder='Confirm Password' name='passwordCheck' type='password' value={this.state.passwordCheck} onChange={this.handleChange} />

          <label htmlFor='accessType'>Access Type:</label>
          {this.state.showError && this.state.formsError.accessType}
          <select id='accessType' name='accessType' value={this.state.accessType} onChange={this.handleChange}>
            <option value='updates'>Updates Only</option>
            <option value='premium'>Premiun Access</option>
          </select>

          <button type='submit'>Create New User</button>
        </form>

        <h2>Show a list of users</h2>
        {this.state.users && this.state.users.map(user => (
            <UserList
              key={user.userId}
              fullName={user.fullName}
              userId={user.userId}
              email={user.email}
              accessType={user.accessType}
              delete={() => this.handleDelete(user.userId)}
            />
          ))}
      </div>;
  }
};

export default Users;
