import React from 'react';
import './users.css';
import Input from '../UI/Input';
import UserList from './UserList';

/**
 * DUMMY DATA
 */
const DATA = [
  {
    fullName: 'John Doe',
    userId: 'john.doe100',
    email: 'john@mail.com',
    accessType: 'updates'
  },
  {
    fullName: 'Jeanne Doe',
    userId: 'jeanne.doe200',
    email: 'jeanne@mail.com',
    accessType: 'updates'
  },
  {
    fullName: 'Bobby Doe',
    userId: 'bobby.doe300',
    email: 'bobby@mail.com',
    accessType: 'updates'
  }
];

const Users = class userInput extends React.Component {
  // state = {
  //   fullName: '',
  //   userId: '',
  //   email: '',
  //   password: '',
  //   passwordCheck: '',
  //   accessType: 'updates',
