  //     email: false,
  //     password: false,
  //     passwordCheck: false,
  //     accessType: false
  //   },
  //   showError: false
  // };

  state = {
    fullName: '',
    userId: '',
    email: '',
    password: '',
    confirmPassword: '',
    showError: ''
  };

  componentDidMount() {
    // Get data from the db
    this.setState({ users: DATA });
  }

  // case 'accessType':
  //   formsError[name] =
  //     value === 'updates'
  //       ? false
  //       : value === 'premium'
  //       ? false
  //       : ERR_SELECT;

  handleChange = e => this.setState({ [e.target.name]: e.target.value });
  hasError = err => err;

  handleSubmit = event => {
    const {} = this.state;
    console.log('handleSubmit');
    event.preventDefault();
    this.setState({ showError: true });

    const data = {
      fullName,
      userId,
      email,
      password
    };

    console.log('UsersPage', this.state);
  };
  // handleSubmit = event => {
  //   event.preventDefault();
  //   const { formsError, password, passwordCheck } = this.state;

  //   // Forms Data to send
  //   const data = { ...this.state };
  //   delete data.formsError;
  //   delete data.passwordCheck;
  //   delete data.showError;

  //   // Check if the form has error
  //   const hasError = Object.values(formsError).find(error => error !== false);
  //   const dataIsEmpty = Object.values(data).includes('');

  //   const passIsEqual = password === passwordCheck;

  //   // If Forms have error show them
  //   if (hasError || !passIsEqual || dataIsEmpty)
  //     this.setState({ showError: true });

  //   // If Forms is valid send data Forms to the server
  //   if (!hasError && passIsEqual && !dataIsEmpty) {
  //     const payload = {
  //       method: 'POST',
  //       body: data
  //     };

  //     fetch('/admin/newuser', payload)
  //       .then(res => console.log(res))
  //       .catch(err => console.log(err));

  //     // FOR TESTING ONLY with dummy data
  //     this.setState({ users: [...this.state.users, data] });
  //   }
  // };

  handleShowUser() {
    this.setState({ showUser: !this.state.showUser });
  }

  handleDelete(userId) {
    const { users } = this.state;
    this.setState({ users: users.filter(user => user.userId != userId) });
  }

  render() {
    return (
      <div>
        <h1>USERS</h1>
        <form onSubmit={this.handleSubmit} style={{ display: 'grid' }}>
          <Input
            label='Full Name'
            placeholder='John Doe'
            name='fullName'
            type='text'
            showError={this.state.showError}
            hasError={this.hasError}
            value={this.state.fullName}
            onChange={this.handleChange}
          />

          <Input
            label='User Id'
            placeholder='john42'
            name='userId'
            type='text'
            showError={this.state.showError}
            hasError={this.hasError}
            onChange={this.handleChange}
            value={this.state.userId}
          />

          <Input
            label='Email'
            placeholder='john.doe@mail.com'
            name='email'
            type='email'
            showError={this.state.showError}
            hasError={this.hasError}
            onChange={this.handleChange}
            value={this.state.email}
          />

          <Input
            label='Password'
            placeholder='Password'
            name='password'
            type='password'
            showError={this.state.showError}
            hasError={this.hasError}
            onChange={this.handleChange}
            value={this.state.password}
          />

          <Input
            label='Confirm Password'
            placeholder='Confirm Password'
            name='confirmPassword'
            type='password'
            showError={this.state.showError}
            hasError={this.hasError}
            onChange={this.handleChange}
            value={this.state.confirmPassword}
          />

          {/* <label htmlFor='accessType'>Access Type:</label>
          {this.state.showError && this.state.formsError.accessType}
          <select
            id='accessType'
            name='accessType'
            value={this.state.accessType}
            onChange={this.handleChange}>
            <option value='updates'>Updates Only</option>
            <option value='premium'>Premiun Access</option>
          </select> */}

          <button type='submit'>Create New User</button>
        </form>

        <h2>Show a list of users</h2>
        {/* {this.state.users &&
          this.state.users.map(user => (
            <UserList
              key={user.userId}
              fullName={user.fullName}
              userId={user.userId}
              email={user.email}
              accessType={user.accessType}
              delete={() => this.handleDelete(user.userId)}
            />
          ))} */}
      </div>
    );
  }
};

export default Users;
