import React from "react";

const ERR_TEXT = "minimum 3 characaters";

class InputText extends React.Component {
  state = {
    input: "",
    error: false,
    minLength: this.props.minLength
  };

  handleChange = event => {
    const { value } = event.target;
    let { error, minLength } = this.state;

    error = value.length < minLength ? ERR_TEXT : false;

    this.setState({ input: value, error });
  };

  render() {
    return (
      <div>
        <label htmlFor={this.props.name}>{this.props.label}</label>
        {this.state.showError && this.state.errorfullName}
        <input
          id={this.props.name}
          placeholder={this.props.placeholder}
          name={this.props.name}
          type='text'
          value={this.state.input}
          onChange={this.handleChange}
          data={this.props.data(this.state.input)}
        />
      </div>
    );
  }
}

export default InputText;
