import React from 'react';
import './users.css';

/**
 * DUMMY DATA
 */
const DATA = [
  {
    id: 'john.doe100',
    fullName: 'John Doe',
    email: 'john@mail.com',
    accessType: 'updates'
  },
  {
    id: 'jeanne.doe200',
    fullName: 'Jeanne Doe',
    email: 'jeanne@mail.com',
    accessType: 'updates'
  },
  {
    id: 'bobby.doe300',
    fullName: 'Bobby Doe',
    email: 'bobby@mail.com',
    accessType: 'updates'
  }
];

class UserList extends React.Component {
  state = {
    users: [],
    hidden: true
  };

  componentDidMount() {
    // Get data from the db
    this.setState({ users: DATA });
  }

  handleShowUser = () => {
    console.log('handleShowUser');

    this.setState({ hidden: !this.state.hidden });
  };

  handleDelete(id) {
    this.setState({ users: this.state.users.filter(user => user.id !== id) });
  }

  render() {
    const User = props => (
      <div>
        <p onClick={() => props.showUser}>
          {props.fullName} {props.UserList}
        </p>
        {/* <div className={this.state.hidden ? 'hidden' : ''}> */}
        <div className={props.hidden ? 'hidden' : ''}>
          <p>
            {props.email} {props.accessType}
          </p>
          <button onClick={props.delete}>Delete User</button>
        </div>
      </div>
    );

    return (
      <div>
        <h2>Show a list of users</h2>
        {this.state.users.map(user => (
          <User
            {...user}
            key={user.id}
            delete={() => this.handleDelete(user.id)}
            showUser={this.handleShowUser}
            // handleToggle={this.handleToggle}
            // hidden={this.state.hidden}
          />
        ))}
      </div>
    );
  }
}

export default UserList;

