import React from 'react';

const ERR_TEXT = 'minimum 3 characaters';

class InputText extends React.Component {
  // state = {
  //   input: '',
  //   error: false,
  //   minLength: this.props.minLength
  // };

  handleError = value => (value.length < 3 ? ERR_TEXT : false);

  // handleChange = event => {
  //   console.log('event', event);

  //   const { value } = event.target;
  //   let { error, minLength } = this.state;

  //   error = value.length < minLength ? ERR_TEXT : false;

  //   this.setState({ input: value, error });
  // };

  render() {
    const { label, name, placeholder, showError } = this.props;
    // const { error } = this.state;
    return (
      <div>
        <label htmlFor={name}>{label}</label>
        {/* {showError ? <span>{error}</span> : null} */}
        <input
          id={name}
          placeholder={placeholder}
          name={name}
          type='text'
          value={this.props.value}
          onChange={this.props.onChange}
          error={this.handleError(this.props.value)}
        />
      </div>
    );
  }
}

export default InputText;
