import React from 'react';
import './users.css';

class UserList extends React.Component {
  state = { hidden: true };

  handleToggle = () => {
    console.log('handleToggle');
    this.setState({ hidden: !this.state.hidden });
  };

  handleDelete(userId) {
    console.log('userId', userId);
    const { users } = this.state;
    this.setState({ users: users.filter(user => user.userId !== userId) });
  }

  render() {
    const User = props => (
      <div>
        <p onClick={props.handleToggle}>
          {props.fullName} {props.UserList}
        </p>
        {/* <div className={this.state.hidden ? 'hidden' : ''}> */}
        <div className={props.hidden ? 'hidden' : ''}>
          <p>
            {props.email} {props.accessType}
          </p>
          <button onClick={() => this.props.delete(props.userId)}>
            Delete User
          </button>
        </div>
      </div>
    );

    return (
      <div>
        <h2>Show a list of users</h2>
        {this.props.users.map(user => (
          <User
            {...user}
            key={user.id}
            handleToggle={this.handleToggle}
            delete={this.handleDelete}
            hidden={this.state.hidden}
          />
        ))}
      </div>
    );
  }
}

export default UserList;

