import React from 'react';

const ERR_SELECT = 'You must choose one option';

class Select extends React.Component {
  handleError = (value, type) => {
    switch (type) {
      case 'text':
        return value.length < 3 ? ERR_TEXT : null;
      case 'email':
        return REGEX_MAIL.test(value) ? null : ERR_MAIL;
      case 'tel':
        return REGEX_TEL.test(value) ? null : ERR_TEL;
      case 'password':
        return value.length < 6 ? ERR_PASS : null;
      default:
        return ERR_FORMS;
    }
  };

  render() {
    const {
      hasError,
      label,
      name,
      onChange,
      showError,
      type,
      value
    } = this.props;

    const error = this.handleError(value, type);

    return (
      <div>
        <label htmlFor={name}>{label}</label>
        {showError ? <span>{error}</span> : null}

        <select id={name} name={name} value={value} onChange={onChange}>
          <option value='updates'>Updates Only</option>
          <option value='premium'>Premiun Access</option>
        </select>
      </div>
    );
  }
}

export default Select;
