import React from 'react';

const formValid = (name, value) => {
  let valid = true;

  switch (name) {
    case 'firstName':
      valid = value.lengh < 3 ? false : true;
      break;

    default:
      valid = true;

      return valid;
  }
};

const Users = class userInput extends React.Component {
  state = {
    fullName: '',
    userId: '',
    email: '',
    password: '',
    passwordCheck: '',
    accessType: 'updates',
    formError: {
      fullName: '',
      userId: '',
      email: '',
      password: '',
      passwordCheck: '',
      accessType: ''
    }
  };

  handleChange = event => {
    const { name, value } = event.target;
    console.log('type', type);

    formValid(name, value);

    this.setState({
      [name]: value
    });
  };

  handleSubmit = event => {
    event.preventDefault();

    console.log('Data form', this.state);
  };

  handleError = () => {};

  render() {
    return (
      <div>
        <h1>USERS</h1>
        <form onSubmit={this.handleSubmit}>
          <label htmlFor='fullName'>Full Name:</label>
          <input
            id='fullName'
            placeholder='Full Name'
            name='fullName'
            type='text'
            value={this.state.fullName}
            onChange={this.handleChange}
          />

          <label htmlFor='userId'>User ID: </label>
          <input
            id='userId'
            placeholder='User ID'
            name='userId'
            type='text'
            value={this.state.userId}
            onChange={this.handleChange}
          />

          <label htmlFor='email'>Email:</label>
          <input
            id='email'
            placeholder='Email'
            name='email'
            type='email'
            value={this.state.email}
            onChange={this.handleChange}
          />

          <label htmlFor='password'>Password:</label>
          <input
            id='password'
            placeholder='Password'
            name='password'
            type='password'
            value={this.state.password}
            onChange={this.handleChange}
          />

          <label htmlFor='passwordCheck'>Confirm Password:</label>
          <input
            id='passwordCheck'
            placeholder='Confirm Password'
            name='passwordCheck'
            type='password'
            value={this.state.passwordCheck}
            onChange={this.handleChange}
          />

          <label htmlFor='accessType'>Access Type:</label>
          <select
            id='accessType'
            name='accessType'
            value={this.state.accessType}
            onChange={this.handleChange}>
            <option value='updates'>Updates Only</option>
            <option value='premium'>Premiun Access</option>
          </select>

          <button type='submit'>Create New User</button>
        </form>
      </div>
    );
  }
};

export default Users;
