import React from 'react';

const ERR_TEXT = 'minimum 3 characaters';

class InputText extends React.Component {
  handleError = value => (value.length < 3 ? ERR_TEXT : false);

  render() {
    const { label, name, onChange, placeholder, showError, value } = this.props;
    const error = this.handleError(value);

    return (
      <div>
        <label htmlFor={name}>{label}</label>
        {showError ? <span>{error}</span> : null}
        <input
          id={name}
          placeholder={placeholder}
          name={name}
          type='text'
          value={value}
          onChange={onChange}
        />
      </div>
    );
  }
}

export default InputText;
